import React, { useEffect, useMemo, useState } from "react";
import { useLocation, Link } from "react-router-dom";
import {
  getMe,
  getNotifications,
  markNotificationRead,
  markAllNotificationsRead,
} from "../api/api";
import "../css/Notifications.css";

export default function Notifications() {
  const { search } = useLocation();
  const qsUnread = new URLSearchParams(search).get("unread") === "true";

  const [me, setMe] = useState(null);
  const [rows, setRows] = useState([]);
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState("");
  const [hideRead, setHideRead] = useState(qsUnread);

  useEffect(() => {
    (async () => {
      try {
        const u = await getMe({ userId: 1 });
        setMe(u);
      } catch (e) {
        setErr(e.message || String(e));
      }
    })();
  }, []);

  async function refetch(unreadOnly = false) {
    if (!me?.userId) return;
    setLoading(true);
    try {
      const list = await getNotifications(me.userId, {
        unread: unreadOnly,
        limit: 200,
      });
      setRows(Array.isArray(list) ? list : []);
      setErr("");
    } catch (e) {
      setErr(e.message || String(e));
    } finally {
      setLoading(false);
    }
  }

  useEffect(() => {
    if (me?.userId) refetch(qsUnread);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [me?.userId, qsUnread]);

  useEffect(() => {
    const onVis = () => {
      if (document.visibilityState === "visible" && me?.userId) refetch(false);
    };
    document.addEventListener("visibilitychange", onVis);
    return () => document.removeEventListener("visibilitychange", onVis);
  }, [me?.userId]);

  const visibleRows = useMemo(() => {
    if (!hideRead) return rows;
    return rows.filter((r) => !r.read);
  }, [rows, hideRead]);

  async function handleMarkOne(id) {
    const prev = rows;
    setRows(rows.map((r) => ((r.notificationId ?? r.id) === id ? { ...r, read: true } : r)));
    try {
      await markNotificationRead(id);
      try { localStorage.setItem("notifications:changed", String(Date.now())); } catch { }
    } catch {
      setRows(prev);
    }
  }

  async function handleMarkAll() {
    if (!me?.userId) return;
    const prev = rows;
    setRows(rows.map((r) => ({ ...r, read: true })));
    try {
      await markAllNotificationsRead(me.userId);
      try { localStorage.setItem("notifications:changed", String(Date.now())); } catch { }
    } catch {
      setRows(prev);
    }
  }

  return (
    <div className="container section">
      <div className="notif-head">
        <h1>Notifikacije</h1>
        <div className="notif-actions">
          <Link className="btn ghost" to="/">Nazad na početnu</Link>
          <button className="btn primary" onClick={handleMarkAll}>
            Označi sve kao pročitano
          </button>
        </div>
      </div>

      {me && (
        <p className="muted">
          {me.firstName} {me.lastName} • {me.userEmail}
        </p>
      )}

      <div className="notif-toolbar">
        <label className="checkbox">
          <input
            type="checkbox"
            checked={hideRead}
            onChange={(e) => setHideRead(e.target.checked)}
          />
          <span>Sakrij pročitane</span>
        </label>
      </div>

      {loading && <div className="notice">Učitavanje…</div>}
      {err && <div className="error">{err}</div>}

      {!loading && !err && (
        <div className="table">
          <div className="thead">
            <div>#</div>
            <div>Naslov</div>
            <div>Poruka</div>
            <div>Poslato</div>
            <div></div>
          </div>
          <div className="tbody">
            {visibleRows.length === 0 ? (
              <div className="empty muted">Nema notifikacija za prikaz.</div>
            ) : (
              visibleRows.map((n, i) => {
                const id = n.notificationId ?? n.id;
                const sent = n.sentAt ? new Date(n.sentAt).toLocaleString() : "";
                return (
                  <div key={id} className={`tr ${n.read ? "is-read" : "is-unread"}`}>
                    <div>{i + 1}</div>
                    <div className="cell-strong">{n.notificationTitle || n.title}</div>
                    <div className="cell-text">
                      {n.message || n.notificationMessage || n.body}
                    </div>
                    <div className="cell-date">{sent}</div>
                    <div className="cell-actions">
                      {!n.read ? (
                        <button className="btn ghost sm" onClick={() => handleMarkOne(id)}>
                          Označi pročitano
                        </button>
                      ) : (
                        <span className="pill">pročitano</span>
                      )}
                    </div>
                  </div>
                );
              })
            )}
          </div>
        </div>
      )}
    </div>
  );
}
