import React, { useEffect, useState } from "react";
import { getMe, getUnreadCount } from "../api/api";
import "../App.css";

export default function Navbar() {
    const [me, setMe] = useState(null);
    const [badge, setBadge] = useState(0);

    useEffect(() => {
        // Ako nema코 auth, prosledi hardkodovani userId ovde (npr. 1) dok ne doda코 login
        getMe({ userId: 1 })
            .then((u) => {
                setMe(u);
                return getUnreadCount(u.userId);
            })
            .then(setBadge)
            .catch(() => { });
    }, []);

    const initials =
        me?.firstName || me?.lastName
            ? `${(me.firstName || "")[0] || ""}${(me.lastName || "")[0] || ""}`.toUpperCase()
            : "U";

    return (
        <header className="nav">
            <div className="container nav-inner">
                <a className="brand" href="/">E-Learning</a>

                <nav className="nav-links">
                    <a href="/catalog">Katalog</a>
                    <a href="/my-courses">Moji kursevi</a>
                </nav>

                <div className="nav-right">
                    <a className="bell" href="/notifications" aria-label="Notifikacije">
                        游댒{badge > 0 && <span className="badge-dot">{badge}</span>}
                    </a>
                    <div className="avatar" title={me?.userEmail || ""}>
                        {me?.avatarUrl ? <img src={me.avatarUrl} alt="Avatar" /> : <span>{initials}</span>}
                    </div>
                </div>
            </div>
        </header>
    );
}
