// src/pages/AddNotification.jsx
import React, { useEffect, useState } from "react";
import {
    Box,
    TextField,
    Button,
    Typography,
    MenuItem,
    FormHelperText,
    CircularProgress,
} from "@mui/material";
import { useNavigate } from "react-router-dom";
import { getMe, getUsers } from "../api/api";
import "../css/AddNotification.css";

// ID admina dok nema auth-a (ne prikazuj njega u combu)
const FAKE_ADMIN_ID = 1;

export default function AddNotification() {
    const navigate = useNavigate();

    const [admin, setAdmin] = useState(null);
    const [users, setUsers] = useState([]); // svi osim admina (FAKE)
    const [loading, setLoading] = useState(false);

    const [form, setForm] = useState({ title: "", message: "", userId: "" });
    const [errors, setErrors] = useState({});
    const [success, setSuccess] = useState("");

    // Učitaj listu korisnika i trenutnog admina; isfiltriraj FAKE_ADMIN_ID iz liste
    useEffect(() => {
        let mounted = true;

        (async () => {
            try {
                // 1) Učitaj sve korisnike
                const allUsers = await getUsers();

                // 2) Učitaj prikaz admina (nije obavezno za filtriranje jer koristimo FAKE_ADMIN_ID,
                //    ali lepo je da imamo ime/email gore ako ti zatreba)
                let me = null;
                try {
                    me = await getMe({ userId: FAKE_ADMIN_ID });
                } catch (_) {
                    // ignoriši ako backend još nije spreman — filter se svakako radi po FAKE_ADMIN_ID
                }

                if (!mounted) return;

                setAdmin(me);

                // Filtriraj: izbaci FAKE admina iz liste
                const filtered = Array.isArray(allUsers)
                    ? allUsers.filter((u) => Number(u.userId) !== Number(FAKE_ADMIN_ID))
                    : [];

                // (opciono) sort po imenu/prezimenu
                filtered.sort((a, b) =>
                    `${a.firstName ?? ""} ${a.lastName ?? ""}`.localeCompare(
                        `${b.firstName ?? ""} ${b.lastName ?? ""}`,
                        "sr"
                    )
                );

                setUsers(filtered);
            } catch (err) {
                console.error("[AddNotification] Greška pri učitavanju korisnika ili me:", err);
            }
        })();

        return () => {
            mounted = false;
        };
    }, []);

    const handleChange = (e) => {
        setForm((f) => ({ ...f, [e.target.name]: e.target.value }));
        setErrors((prev) => ({ ...prev, [e.target.name]: "" }));
        setSuccess("");
    };

    const validate = () => {
        const newErrors = {};
        if (!form.title.trim()) newErrors.title = "Naslov je obavezan.";
        if (!form.message.trim()) newErrors.message = "Poruka je obavezna.";
        if (!form.userId) newErrors.userId = "Morate izabrati korisnika.";
        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!validate()) return;

        setLoading(true);
        setSuccess("");
        try {
            const res = await fetch("/api/notifications", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({
                    userId: Number(form.userId),
                    notificationTitle: form.title.trim(),
                    notificationMessage: form.message.trim(),
                }),
            });

            if (!res.ok) throw new Error("Greška pri slanju notifikacije.");

            setSuccess("Notifikacija uspešno poslata!");
            setForm({ title: "", message: "", userId: "" });
        } catch (err) {
            console.error(err);
            setErrors({ submit: "Došlo je do greške pri slanju notifikacije." });
        } finally {
            setLoading(false);
        }
    };

    return (
        <Box component="form" onSubmit={handleSubmit} className="addn-wrap">
            <Typography className="addn-title" variant="h5">
                📢 Pošalji novu notifikaciju
            </Typography>

            <TextField
                className="addn-field"
                label="Naslov notifikacije"
                name="title"
                value={form.title}
                onChange={handleChange}
                error={!!errors.title}
                helperText={errors.title}
                fullWidth
            />

            <TextField
                className="addn-field"
                label="Poruka"
                name="message"
                value={form.message}
                onChange={handleChange}
                error={!!errors.message}
                helperText={errors.message}
                multiline
                rows={4}
                fullWidth
            />

            <TextField
                className="addn-field"
                select
                label="Korisnik"
                name="userId"
                value={form.userId}
                onChange={handleChange}
                error={!!errors.userId}
                helperText={errors.userId || "Izaberite kome šaljete poruku (svi osim vas)."}
                fullWidth
            >
                <MenuItem value="" disabled>
                    — Izaberi korisnika —
                </MenuItem>
                {users.map((u) => (
                    <MenuItem key={u.userId} value={u.userId}>
                        {u.firstName} {u.lastName} • {u.userEmail}
                    </MenuItem>
                ))}
            </TextField>

            {errors.submit && (
                <FormHelperText error className="addn-helper">
                    {errors.submit}
                </FormHelperText>
            )}

            {success && <FormHelperText className="addn-success">✅ {success}</FormHelperText>}

            <Box className="addn-actions">
                <Button
                    className="addn-cancel"
                    variant="outlined"
                    color="secondary"
                    onClick={() => navigate("/notifications")}
                >
                    Odustani
                </Button>

                <Button
                    className="addn-submit"
                    variant="contained"
                    color="primary"
                    type="submit"
                    disabled={loading}
                >
                    {loading ? <CircularProgress size={22} color="inherit" /> : "Pošalji"}
                </Button>
            </Box>
        </Box>
    );
}
