// src/App.js
import React from "react";
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";

import Navbar from "./components/Navbar";
import Footer from "./components/Footer";
import ProtectedRoute from "./components/ProtectedRoute";

import Home from "./pages/Home";
import Notifications from "./pages/Notifications";
import AddNotification from "./pages/AddNotification";
import Login from "./pages/Login";
import Register from "./pages/Register";

import "./App.css";

export default function App() {
  const hasToken = typeof window !== "undefined" && !!localStorage.getItem("token");

  return (
    <BrowserRouter>
      {/* Navbar može da postoji i na /login, ali ako želiš „čistu“ login stranu,
          samo ga uslovi tokenom: */}
      {hasToken && <Navbar />}

      <Routes>
        {/* Zaštićene rute */}
        <Route
          path="/"
          element={
            <ProtectedRoute>
              <Home />
            </ProtectedRoute>
          }
        />
        <Route
          path="/notifications"
          element={
            <ProtectedRoute>
              <Notifications />
            </ProtectedRoute>
          }
        />
        <Route
          path="/notifications/new"
          element={
            <ProtectedRoute>
              <AddNotification />
            </ProtectedRoute>
          }
        />

        {/* Javne rute */}
        <Route
          path="/login"
          element={<Login onSuccess={() => (window.location.href = "/")} />}
        />
        <Route
          path="/register"
          element={<Register onSuccess={() => (window.location.href = "/")} />}
        />

        {/* Fallback: ako neko ode na nepostojeću rutu, pošalji na / ili /login */}
        <Route path="*" element={<Navigate to={hasToken ? "/" : "/login"} replace />} />
      </Routes>

      {hasToken && <Footer />}
    </BrowserRouter>
  );
}
