// src/components/Navbar.jsx
import React, { useEffect, useState } from "react";
import { Link, NavLink } from "react-router-dom";
import { getMe, getUnreadCount } from "../api/api";
import { onUnreadChanged } from "../api/notificationsBus";
import "../App.css";

// Za test dok nema auth-a:
const FAKE_USER_ID = 2;

export default function Navbar() {
  const [me, setMe] = useState(null);
  const [badge, setBadge] = useState(0);

  useEffect(() => {
    let isMounted = true;

    async function load() {
      try {
        const u = await getMe({ userId: FAKE_USER_ID });
        if (!isMounted) return;
        setMe(u);

        const c = await getUnreadCount(u.userId);
        if (!isMounted) return;
        setBadge(c);
      } catch { /* ignore */ }
    }

    load();

    const off = onUnreadChanged(async () => {
      try {
        const id = me?.userId ?? FAKE_USER_ID;
        const c = await getUnreadCount(id);
        if (!isMounted) return;
        setBadge(c);
      } catch { /* ignore */ }
    });

    return () => { isMounted = false; off?.(); };
  }, [me?.userId]);

  const username =
    me?.username ||
    [me?.firstName, me?.lastName].filter(Boolean).join(" ") ||
    "Korisnik";

  const active = ({ isActive }) => ({
    textDecoration: "none",
    opacity: isActive ? 1 : 0.85,
  });

  return (
    <nav className="nav">
      <div className="container nav-inner">
        <Link to="/" className="brand">E-Learning</Link>

        <div className="nav-links">
          <NavLink to="/" style={active}>Poƒçetna</NavLink>
          <NavLink to="/catalog" style={active}>Katalog</NavLink>
          <NavLink to="/my-courses" style={active}>Moji kursevi</NavLink>
        </div>

        <div className="nav-right">
          <Link to="/notifications" className="bell" title="Notifikacije">
            üîî
            {badge > 0 && <span className="badge-dot">{badge}</span>}
          </Link>
          <span className="user-chip" title={username}>{username}</span>
        </div>
      </div>
    </nav>
  );
}
