// src/pages/AddNotification.jsx
import React, { useEffect, useState } from "react";
import {
    Box,
    TextField,
    Button,
    Typography,
    MenuItem,
    FormHelperText,
    CircularProgress,
} from "@mui/material";
import { getMe } from "../api/api";
import "../css/AddNotification.css";

// Adminov ID za test dok nema auth-a:
const FAKE_ADMIN_ID = 1;

export default function AddNotification() {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(false);
    const [form, setForm] = useState({ title: "", message: "", userId: "" });
    const [errors, setErrors] = useState({});
    const [success, setSuccess] = useState("");
    const [admin, setAdmin] = useState(null);

    // Učitavanje admina i korisnika
    useEffect(() => {
        async function loadData() {
            try {
                const res = await fetch("/api/users");
                const usersData = await res.json();

                const me = await getMe({ userId: FAKE_ADMIN_ID });
                setAdmin(me);

                const filtered = Array.isArray(usersData)
                    ? usersData.filter((u) => u.userId !== me.userId)
                    : [];
                setUsers(filtered);
            } catch (err) {
                console.error("Greška pri učitavanju korisnika:", err);
            }
        }
        loadData();
    }, []);

    const handleChange = (e) => {
        setForm({ ...form, [e.target.name]: e.target.value });
        setErrors({ ...errors, [e.target.name]: "" });
        setSuccess("");
    };

    const validate = () => {
        const newErrors = {};
        if (!form.title.trim()) newErrors.title = "Naslov je obavezan.";
        if (!form.message.trim()) newErrors.message = "Poruka je obavezna.";
        if (!form.userId) newErrors.userId = "Morate izabrati korisnika.";
        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!validate()) return;

        setLoading(true);
        setSuccess("");
        try {
            const res = await fetch("/api/notifications", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({
                    userId: form.userId,
                    notificationTitle: form.title,
                    notificationMessage: form.message,
                }),
            });

            if (!res.ok) throw new Error("Greška pri slanju notifikacije.");
            setSuccess("Notifikacija uspešno poslata!");
            setForm({ title: "", message: "", userId: "" });
        } catch (err) {
            setErrors({ submit: "Došlo je do greške pri slanju notifikacije." });
        } finally {
            setLoading(false);
        }
    };

    return (
        <Box component="form" onSubmit={handleSubmit} className="addn-wrap">
            <Typography className="addn-title" variant="h5">
                📢 Pošalji novu notifikaciju
            </Typography>

            <TextField
                className="addn-field"
                label="Naslov notifikacije"
                name="title"
                value={form.title}
                onChange={handleChange}
                error={!!errors.title}
                helperText={errors.title}
                fullWidth
            />

            <TextField
                className="addn-field"
                label="Poruka"
                name="message"
                value={form.message}
                onChange={handleChange}
                error={!!errors.message}
                helperText={errors.message}
                multiline
                rows={4}
                fullWidth
            />

            <TextField
                className="addn-field"
                select
                label="Korisnik"
                name="userId"
                value={form.userId}
                onChange={handleChange}
                error={!!errors.userId}
                helperText={errors.userId || "Izaberite kome želite da pošaljete poruku."}
                fullWidth
            >
                {users.map((u) => (
                    <MenuItem key={u.userId} value={u.userId}>
                        {u.firstName} {u.lastName} • {u.userEmail}
                    </MenuItem>
                ))}
            </TextField>

            {errors.submit && (
                <FormHelperText error className="addn-helper">
                    {errors.submit}
                </FormHelperText>
            )}

            {success && (
                <FormHelperText className="addn-success">
                    ✅ {success}
                </FormHelperText>
            )}

            <Box className="addn-actions">
                <Button
                    className="addn-submit"
                    variant="contained"
                    color="primary"
                    type="submit"
                    disabled={loading}
                >
                    {loading ? <CircularProgress size={22} color="inherit" /> : "Pošalji"}
                </Button>
            </Box>
        </Box>
    );
}
