// src/pages/Notifications.jsx
import React, { useEffect, useMemo, useState } from "react";
import {
  getMe,
  getNotifications,
  markNotificationRead,
  markAllNotificationsRead,
  getNotificationTypes,
} from "../api/api";
import "../css/Notifications.css";

export default function Notifications() {
  const [me, setMe] = useState(null);
  const [items, setItems] = useState([]);
  const [types, setTypes] = useState([]);           // [{id, name}]
  const [typesMap, setTypesMap] = useState(new Map()); // id -> name
  const [loading, setLoading] = useState(true);
  const [marking, setMarking] = useState({});
  const [unreadOnly, setUnreadOnly] = useState(false);
  const [q, setQ] = useState("");
  const [typeId, setTypeId] = useState("");         // filter po tipu
  const [sort, setSort] = useState("sentAt");
  const [dir, setDir] = useState("DESC");

  // učitaj korisnika, tipove i notifikacije
  useEffect(() => {
    let uid;
    (async () => {
      try {
        const u = await getMe({ userId: 1 }); // dok nema auth-a
        setMe(u);
        uid = u.userId;

        // paralelno
        const [typeList] = await Promise.all([getNotificationTypes()]);
        setTypes(typeList || []);
        setTypesMap(new Map((typeList || []).map(t => [String(t.id), t.name])));

        await load(uid);
      } catch (e) {
        console.error(e);
      }
    })();
  }, []);

  async function load(uid = me?.userId) {
    if (!uid) return;
    setLoading(true);
    try {
      const data = await getNotifications(uid, {
        unreadOnly,
        limit: 50,
        q,
        typeId,
        sort,
        dir,
      });
      setItems(Array.isArray(data) ? data : []);
    } catch (e) {
      console.error(e);
      setItems([]);
    } finally {
      setLoading(false);
    }
  }

  // refetch kad filter/sort promene
  useEffect(() => {
    if (me?.userId) load(me.userId);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [unreadOnly, q, typeId, sort, dir]);

  async function onMarkOne(id) {
    setMarking(m => ({ ...m, [id]: true }));
    try {
      await markNotificationRead(id);
      await load();
    } catch (e) {
      console.error(e);
    } finally {
      setMarking(m => ({ ...m, [id]: false }));
    }
  }

  async function onMarkAll() {
    if (!me?.userId) return;
    setLoading(true);
    try {
      await markAllNotificationsRead(me.userId);
      await load();
    } catch (e) {
      console.error(e);
    } finally {
      setLoading(false);
    }
  }

  const sortOptions = [
    { key: "sentAt", label: "Datum (novije prvo)", dirDefault: "DESC" },
    { key: "title", label: "Naslov A–Z", dirDefault: "ASC" },
    { key: "title", label: "Naslov Z–A", dirDefault: "DESC" },
    { key: "typeName", label: "Tip A–Z", dirDefault: "ASC" },
    { key: "typeName", label: "Tip Z–A", dirDefault: "DESC" },
  ];

  function onSortChange(e) {
    const val = e.target.value; // npr "title|ASC"
    const [k, d] = val.split("|");
    setSort(k);
    setDir(d);
  }

  const sortValue = useMemo(() => `${sort}|${dir}`, [sort, dir]);

  return (
    <div className="container section">
      <div className="notif-head">
        <div className="notif-title">
          <h1>Notifikacije</h1>
          {me && (
            <div className="muted">
              {me.firstName} {me.lastName} • {me.userEmail}
            </div>
          )}
        </div>

        <div className="notif-actions">
          <button className="btn ghost" onClick={() => window.history.back()}>
            Nazad na početnu
          </button>
          <button className="btn primary" onClick={onMarkAll}>
            Označi sve kao pročitano
          </button>
        </div>
      </div>

      {/* FILTER TRAKA */}
      <div className="filters">
        <input
          className="search"
          placeholder="Pretraga po naslovu, poruci…"
          value={q}
          onChange={(e) => setQ(e.target.value)}
        />

        <label className="chk">
          <input
            type="checkbox"
            checked={unreadOnly}
            onChange={(e) => setUnreadOnly(e.target.checked)}
          />
          <span>Samо nepročitane</span>
        </label>

        {/* filter po tipu */}
        <select
          className="select"
          value={typeId}
          onChange={(e) => setTypeId(e.target.value)}
        >
          <option value="">Svi tipovi</option>
          {types.map(t => (
            <option key={t.id} value={t.id}>{t.name}</option>
          ))}
        </select>

        {/* sort */}
        <select className="select" value={sortValue} onChange={onSortChange}>
          {sortOptions.map(o => (
            <option key={`${o.key}|${o.dirDefault}`} value={`${o.key}|${o.dirDefault}`}>
              {o.label}
            </option>
          ))}
        </select>
      </div>

      <div className="card">
        {loading ? (
          <div className="skeleton-list">
            <div className="skeleton" />
            <div className="skeleton" />
            <div className="skeleton" />
          </div>
        ) : (
          <table className="notif-table">
            <thead>
              <tr>
                <th>#</th>
                <th>Naslov</th>
                <th>Tip</th>
                <th>Poruka</th>
                <th>Poslato</th>
                <th />
              </tr>
            </thead>
            <tbody>
              {items.map((n, idx) => {
                const id = n.notificationId ?? n.id;
                const title = n.notificationTitle ?? n.title ?? "";
                const message = n.message ?? n.notificationMessage ?? "";
                const sentAt = n.sentAt ? new Date(n.sentAt) : null;

                // pokušaj 3 izvora za naziv tipa:
                const typeName =
                  n.typeName ||
                  n.type?.name ||
                  typesMap.get(String(n.typeId || n.type?.id)) ||
                  "—";

                return (
                  <tr key={id}>
                    <td>{idx + 1}</td>
                    <td>{title}</td>
                    <td>{typeName}</td>
                    <td>{message}</td>
                    <td>
                      {sentAt
                        ? sentAt.toLocaleDateString() + ", " + sentAt.toLocaleTimeString()
                        : "—"}
                    </td>
                    <td style={{ textAlign: "right" }}>
                      {n.read ? (
                        <span className="pill">Pročitano</span>
                      ) : (
                        <button
                          className="btn ghost sm"
                          disabled={!!marking[id]}
                          onClick={() => onMarkOne(id)}
                          title="Označi pročitano"
                        >
                          {marking[id] ? "…" : "Označi pročitano"}
                        </button>
                      )}
                    </td>
                  </tr>
                );
              })}
              {items.length === 0 && !loading && (
                <tr>
                  <td colSpan={6} className="muted">
                    Nema rezultata.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}
