import React, { useState } from "react";
import { request } from "../api/fetcher";
import "../css/auth.css";

export default function Login({ onSuccess }) {
    const [form, setForm] = useState({ username: "", password: "" });
    const [loading, setLoading] = useState(false);
    const [err, setErr] = useState("");
    const [ok, setOk] = useState("");

    async function handleSubmit(e) {
        e.preventDefault();
        setErr(""); setOk(""); setLoading(true);
        try {
            const { token, user } = await request("/auth/login", {
                method: "POST",
                body: { username: form.username.trim(), password: form.password },
            });
            if (!token) throw new Error("Token missing in response");
            localStorage.setItem("token", token);
            if (user) localStorage.setItem("user", JSON.stringify(user));
            setOk("Welcome back!");
            if (typeof onSuccess === "function") onSuccess(user);
            else window.location.href = "/";
        } catch (e2) {
            setErr(e2.message || "Login failed. Please try again.");
        } finally {
            setLoading(false);
        }
    }

    return (
        <div className="auth-wrap">
            <div className="auth-card">
                <h2>Welcome back</h2>
                <p className="muted">Sign in to continue</p>

                {err && <div className="auth-alert">{err}</div>}
                {ok && <div className="auth-success">{ok}</div>}

                <form onSubmit={handleSubmit} className="auth-form" autoComplete="on">
                    <div className="field">
                        <label>Username</label>
                        <input
                            type="text"
                            autoComplete="username"
                            value={form.username}
                            onChange={(e) => setForm((f) => ({ ...f, username: e.target.value }))}
                            required
                        />
                    </div>

                    <div className="field">
                        <label>Password</label>
                        <input
                            type="password"
                            autoComplete="current-password"
                            value={form.password}
                            onChange={(e) => setForm((f) => ({ ...f, password: e.target.value }))}
                            required
                            minLength={6}
                        />
                    </div>

                    <button className="btn-primary" disabled={loading}>
                        {loading ? "Signing in…" : "Sign in"}
                    </button>
                </form>

                <div className="auth-footer">
                    <span className="muted">Don’t have an account? </span>
                    <a href="/register">Create one</a>
                </div>
            </div>
        </div>
    );
}
