import React, { useEffect, useMemo, useState } from "react";
import {
  getMe,  // vidi tačku 2 ispod (API)
  getNotifications,
  getUnreadCount,
  markNotificationRead,
  markAllNotificationsRead, // vidi tačku 2 ispod (API)
} from "../api/api";
import "../css/Notifications.css";

export default function Notifications() {
  const [me, setMe] = useState(null);
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [busy, setBusy] = useState(false);
  const [error, setError] = useState("");

  // Lokalni format datuma (SR)
  const fmt = useMemo(
    () =>
      new Intl.DateTimeFormat("sr-RS", {
        day: "2-digit",
        month: "2-digit",
        year: "numeric",
        hour: "2-digit",
        minute: "2-digit",
      }),
    []
  );

  useEffect(() => {
    let uid;
    getMe({ userId: 1 }) // dok nema auth-a
      .then((u) => {
        setMe(u);
        uid = u.userId;
        // ⬇⬇⬇ BITNO: prosleđujemo userId
        return getNotifications(uid, { unread: false, limit: 50 });
      })
      .then((list) => setItems(list || []))
      .finally(() => setLoading(false));
  }, []);

  // helper: osveži badge u navbaru
  async function refreshNavbarBadge(userId) {
    try {
      const n = await getUnreadCount(userId);
      window.dispatchEvent(new CustomEvent("unread-updated", { detail: n }));
    } catch {
      // ignore
    }
  }

  async function handleMarkOne(id) {
    if (!me?.userId) return;
    setBusy(true);
    const prev = items;
    setItems((arr) => arr.filter((x) => (x.notificationId ?? x.id) !== id));

    try {
      await markNotificationRead(id);
      await refreshNavbarBadge(me.userId);
    } catch (e) {
      setItems(prev); // vrati na staro ako pukne
      setError(e?.message || "Greška pri označavanju.");
    } finally {
      setBusy(false);
    }
  }

  async function handleMarkAll() {
    if (!me?.userId) return;
    setBusy(true);
    try {
      await markAllNotificationsRead(me.userId);
      setItems([]); // sve pročitane -> prazno
      await refreshNavbarBadge(me.userId);
    } catch (e) {
      setError(e?.message || "Greška pri označavanju svih.");
    } finally {
      setBusy(false);
    }
  }

  return (
    <div className="container section">
      <div className="page-head">
        <h1>Notifikacije</h1>

        <div className="page-actions">
          <a className="btn ghost" href="/">Nazad na početnu</a>
          <button className="btn primary" onClick={handleMarkAll} disabled={busy || items.length === 0}>
            Označi sve kao pročitano
          </button>
        </div>
      </div>

      {me && (
        <div className="muted" style={{ marginBottom: 12 }}>
          {me.firstName} {me.lastName} • {me.userEmail}
        </div>
      )}

      {error && <div className="notice error">{error}</div>}
      {loading && <div className="notice">Učitavanje korisnika...</div>}

      {!loading && !error && items.length === 0 && (
        <div className="notice empty">Nema notifikacija.</div>
      )}

      {!loading && !error && items.length > 0 && (
        <div className="card">
          <div className="table-wrap">
            <table className="table">
              <thead>
                <tr>
                  <th>#</th>
                  <th>Naslov</th>
                  <th>Poruka</th>
                  <th>Poslato</th>
                  <th />
                </tr>
              </thead>
              <tbody>
                {items.map((n, idx) => {
                  const id = n.notificationId ?? n.id;
                  const title = n.notificationTitle ?? n.title ?? "—";
                  const text = n.message ?? n.notificationMessage ?? n.body ?? "—";
                  const sent = n.sentAt ? fmt.format(new Date(n.sentAt)) : "—";

                  return (
                    <tr key={id}>
                      <td>{idx + 1}</td>
                      <td className="strong">{title}</td>
                      <td>{text}</td>
                      <td>{sent}</td>
                      <td className="ta-right">
                        <button
                          className="btn ghost sm"
                          onClick={() => handleMarkOne(id)}
                          disabled={busy}
                        >
                          Označi pročitano
                        </button>
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        </div>
      )}
    </div>
  );
}
