// src/pages/Home.jsx
import React, { useEffect, useState } from "react";
import { getMe, getNotifications, getRecommendedCourses } from "../api/api";
import "../App.css";
import "../css/Home.css";

// JEDINO mesto gde bira≈° korisnika dok nema auth-a
const FAKE_USER_ID = 2;

export default function Home() {
  const [me, setMe] = useState(null);
  const [notes, setNotes] = useState([]);
  const [recs, setRecs] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const uid = FAKE_USER_ID; // ne oslanjamo se na getMe za ID ‚Äì da izolujemo problem

    (async () => {
      try {
        // 1) i dalje uƒçitamo prikaz korisnika (ƒçisto za ime/email u hero-u)
        const u = await getMe({ userId: uid });
        setMe(u);
        console.log("[HOME] getMe ->", u);

        // 2) striktno tra≈æimo samo neproƒçitane, limit 3
        const unreadList = await getNotifications({ userId: uid, unread: true, limit: 3 });
        console.log(
          "[HOME] /api/notifications?userId=%s&unread=true&limit=3 ->",
          uid,
          unreadList
        );

        // 3) preporuƒçeni kursevi (stub)
        const r = await getRecommendedCourses();

        setNotes(Array.isArray(unreadList) ? unreadList : []);
        setRecs(Array.isArray(r) ? r : []);
      } catch (e) {
        console.error("[HOME] load error:", e);
        setNotes([]); // prazan fallback
      } finally {
        setLoading(false);
      }
    })();
  }, []);

  return (
    <>
      <section className="hero hero--compressed">
        <div className="container hero-grid">
          <div className="hero-copy">
            <h1>ƒÜao{me?.firstName ? `, ${me.firstName}` : ""}! üëã</h1>
            <p className="muted">Dobrodo≈°ao na svoju kontrolnu tablu.</p>

            <div className="quick-actions">
              <a className="btn primary" href="/my-courses">Idi na ‚ÄúMoji kursevi‚Äù</a>
              <a className="btn ghost" href="/catalog">Pogledaj katalog</a>
            </div>
          </div>

          <div className="profile-card">
            <div className="avatar xl">
              {me?.avatarUrl ? (
                <img src={me.avatarUrl} alt="Avatar" />
              ) : (
                <span>
                  {(me?.firstName?.[0] || "U").toUpperCase()}
                  {(me?.lastName?.[0] || "").toUpperCase()}
                </span>
              )}
            </div>
            <div className="profile-meta">
              <div className="profile-name">
                {me?.firstName} {me?.lastName}
              </div>
              <div className="muted">{me?.userEmail}</div>
            </div>
          </div>
        </div>
      </section>

      <section className="section">
        <div className="container grid-2">
          <div className="card">
            <div className="card-head">
              <h3>Neproƒçitane notifikacije</h3>
              <a className="link" href="/notifications">Vidi sve</a>
            </div>

            {loading ? (
              <div className="skeleton-list">
                <div className="skeleton" />
                <div className="skeleton" />
                <div className="skeleton" />
              </div>
            ) : notes.length === 0 ? (
              <p className="muted">Nema novih notifikacija.</p>
            ) : (
              <ul className="note-list" role="list">
                {notes.map((n) => {
                  const id = n.notificationId ?? n.id;
                  return (
                    <li key={id} className="note-item" role="listitem">
                      <div className="note-main">
                        <div className="note-title">{n.notificationTitle || n.title}</div>
                        <div className="note-text muted">
                          {n.message || n.notificationMessage || n.body}
                        </div>
                      </div>
                    </li>
                  );
                })}
              </ul>
            )}
          </div>

          <div className="card">
            <div className="card-head">
              <h3>Preporuƒçeni kursevi</h3>
              <a className="link" href="/catalog">Katalog</a>
            </div>
            <div className="courses">
              {recs.map((c) => (
                <a key={c.id} className="course" href={`/course/${c.id}`}>
                  <div className="thumb" />
                  <div className="course-title">{c.title}</div>
                  <div className="muted">{c.meta}</div>
                </a>
              ))}
              {recs.length === 0 && !loading && (
                <p className="muted">Uskoro preporuke.</p>
              )}
            </div>
          </div>
        </div>
      </section>
    </>
  );
}
