// src/components/Navbar.jsx
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { getMe, getUnreadCount } from "../api/api";
import { onUnreadChanged } from "../api/notificationsBus";
import "../App.css"; // ili tvoj css

// Za test dok nema auth-a:
const FAKE_USER_ID = 2;

export default function Navbar() {
  const [me, setMe] = useState(null);
  const [badge, setBadge] = useState(0);

  useEffect(() => {
    let isMounted = true;

    async function load() {
      try {
        const u = await getMe({ userId: FAKE_USER_ID }); // <<— PAZI DA JE 2 AKO TI JE KORISNIK #2
        if (!isMounted) return;
        setMe(u);

        const c = await getUnreadCount(u.userId);
        if (!isMounted) return;
        setBadge(c);
      } catch {
        // ignoriši
      }
    }

    load();

    // slušaj promene badge-a iz drugih delova app-a (Home, Notifications…)
    const off = onUnreadChanged(async () => {
      try {
        // koristi već učitanog me (ako ga ima)
        const id = me?.userId ?? FAKE_USER_ID;
        const c = await getUnreadCount(id);
        if (!isMounted) return;
        setBadge(c);
      } catch { /* ignore */ }
    });

    return () => {
      isMounted = false;
      off?.();
    };
  }, [me?.userId]);

  return (
    <nav className="nav">
      <div className="container nav-inner">
        <Link to="/" className="brand">E-Learning</Link>

        <div className="nav-links">
          <Link to="/catalog">Katalog</Link>
          <Link to="/my-courses">Moji kursevi</Link>

          <Link to="/notifications" className="bell" title="Notifikacije">
            🔔
            {badge > 0 && <span className="badge-dot">{badge}</span>}
          </Link>

          <div className="avatar">
            {me ? `${(me.firstName || "")[0] || "U"}${(me.lastName || "")[0] || ""}` : "U"}
          </div>
        </div>
      </div>
    </nav>
  );
}
