// src/pages/Home.jsx
import React, { useEffect, useState } from "react";
import { getMe, getNotifications, markNotificationRead } from "../api/api";
import "../App.css";
import "../css/Home.css";

export default function Home() {
  const [me, setMe] = useState(null);
  const [notes, setNotes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [marking, setMarking] = useState({}); // { [id]: boolean }

  useEffect(() => {
    let mounted = true;
    (async () => {
      try {
        // dok nema auth-a, guramo ?id=1 (ti promeni po potrebi)
        const u = await getMe({ userId: 1 });
        if (!mounted) return;
        setMe(u);

        const preview = await getNotifications({
          userId: u.userId,
          unread: true,
          limit: 3,
        });
        if (!mounted) return;
        setNotes(preview || []);
      } catch (e) {
        // mo≈æe≈° dodati toast/log
        console.error(e);
      } finally {
        if (mounted) setLoading(false);
      }
    })();
    return () => (mounted = false);
  }, []);

  async function handleRead(id) {
    setMarking((m) => ({ ...m, [id]: true }));
    const prev = notes;
    setNotes((arr) => arr.filter((x) => (x.notificationId ?? x.id) !== id));
    try {
      await markNotificationRead(id);
    } catch (e) {
      // revert on error
      setNotes(prev);
      console.error(e);
    } finally {
      setMarking((m) => ({ ...m, [id]: false }));
    }
  }

  return (
    <>
      <section className="hero hero--compressed">
        <div className="container hero-grid">
          <div className="hero-copy">
            <h1>ƒÜao{me?.firstName ? `, ${me.firstName}` : ""}! üëã</h1>
            <p className="muted">Dobrodo≈°ao na svoju kontrolnu tablu.</p>

            <div className="quick-actions">
              <a className="btn primary" href="/my-courses">Idi na ‚ÄúMoji kursevi‚Äù</a>
              <a className="btn ghost" href="/catalog">Pogledaj katalog</a>
            </div>
          </div>

          <div className="profile-card">
            <div className="avatar xl">
              {me?.avatarUrl ? (
                <img src={me.avatarUrl} alt="Avatar" />
              ) : (
                <span>
                  {(me?.firstName?.[0] || "U").toUpperCase()}
                  {(me?.lastName?.[0] || "").toUpperCase()}
                </span>
              )}
            </div>
            <div className="profile-meta">
              <div className="profile-name">
                {me?.firstName} {me?.lastName}
              </div>
              <div className="muted">{me?.userEmail}</div>
            </div>
          </div>
        </div>
      </section>

      <section className="section">
        <div className="container grid-2">
          <div className="card">
            <div className="card-head">
              <h3>Neproƒçitane notifikacije</h3>
              <a className="link" href="/notifications">Vidi sve</a>
            </div>

            {loading ? (
              <div className="skeleton-list">
                <div className="skeleton" />
                <div className="skeleton" />
                <div className="skeleton" />
              </div>
            ) : notes.length === 0 ? (
              <p className="muted">Nema novih notifikacija.</p>
            ) : (
              <ul className="note-list" role="list">
                {notes.map((n) => {
                  const id = n.notificationId ?? n.id;
                  return (
                    <li key={id} className="note-item" role="listitem">
                      <div className="note-main">
                        <div className="note-title">{n.notificationTitle || n.title}</div>
                        <div className="note-text muted">
                          {n.message || n.notificationMessage || n.body}
                        </div>
                      </div>
                      <button
                        className="btn ghost sm"
                        disabled={!!marking[id]}
                        onClick={() => handleRead(id)}
                      >
                        {marking[id] ? "..." : "Oznaƒçi proƒçitano"}
                      </button>
                    </li>
                  );
                })}
              </ul>
            )}
          </div>

          <div className="card">
            <div className="card-head">
              <h3>Preporuƒçeni kursevi</h3>
              <a className="link" href="/catalog">Katalog</a>
            </div>
            <div className="courses">
              {/* placeholder ‚Äì kasnije ƒáemo vezati na /api/courses/recommended */}
              <a className="course" href="#">
                <div className="thumb" />
                <div className="course-title">Java za poƒçetnike</div>
                <div className="muted">8h ‚Ä¢ 24 lekcije</div>
              </a>
              <a className="course" href="#">
                <div className="thumb" />
                <div className="course-title">Spring Boot REST API</div>
                <div className="muted">6h ‚Ä¢ 18 lekcija</div>
              </a>
              <a className="course" href="#">
                <div className="thumb" />
                <div className="course-title">React osnove</div>
                <div className="muted">5h ‚Ä¢ 15 lekcija</div>
              </a>
            </div>
          </div>
        </div>
      </section>
    </>
  );
}
