import http from "./http";

export async function getMe() {
  const { data } = await http.get("/users/me");
  return data;
}

export async function getUnreadCount() {
  try {
    const { data } = await http.get("/notifications/unread/count");
    return Number(data || 0);
  } catch {
    return 0;
  }
}

export async function getNotifications({ unread = false, limit = 50 } = {}) {
  const { data } = await http.get("/notifications", {
    params: { unread: unread ? "true" : undefined, limit },
  });
  return data;
}

export async function markNotificationRead(id) {
  await http.post(`/notifications/${id}/read`);
  return true;
}

export async function markAllNotificationsRead() {
  await http.post(`/notifications/read-all`);
  return true;
}

export async function getNotificationTypes() {
  const { data } = await http.get("/notification-types");
  return data;
}

export async function deleteNotification(id) {
  await http.delete(`/notifications/${id}`);
  return true;
}

export async function getUsers() {
  const { data } = await http.get("/users");
  return data;
}

export function getRecommendedCourses() {
  return Promise.resolve([
    { id: 1, title: "Java for Beginners", meta: "8h • 24 lessons" },
    { id: 2, title: "Spring Boot REST API", meta: "6h • 18 lessons" },
    { id: 3, title: "React Essentials", meta: "5h • 15 lessons" },
  ]);
}
