// src/pages/Notifications.jsx
import React, { useEffect, useState } from "react";
import { getMe, getNotifications, markNotificationRead, markAllNotificationsRead } from "../api/api";
import "../css/Notifications.css";

export default function Notifications() {
  const [me, setMe] = useState(null);
  const [rows, setRows] = useState([]);
  const [loading, setLoading] = useState(true);
  const [marking, setMarking] = useState({}); // { [id]: true }
  const [error, setError] = useState("");

  useEffect(() => {
    let mounted = true;
    (async () => {
      try {
        // Dok nema auth, uzmi fiksni sample usera (npr. id=1) ili zameni kako želiš
        const u = await getMe({ userId: 1 });
        if (!mounted) return;
        setMe(u);

        const list = await getNotifications({ userId: u.userId, unread: false, limit: 200 });
        if (!mounted) return;
        setRows(list || []);
      } catch (e) {
        setError(e?.response?.data?.message || e.message || "Greška pri učitavanju.");
      } finally {
        if (mounted) setLoading(false);
      }
    })();
    return () => (mounted = false);
  }, []);

  async function handleMarkRead(id) {
    setMarking((m) => ({ ...m, [id]: true }));
    const prev = rows;
    setRows((arr) => arr.filter((r) => (r.notificationId ?? r.id) !== id));
    try {
      await markNotificationRead(id);
    } catch (e) {
      setRows(prev); // revert on error
    } finally {
      setMarking((m) => ({ ...m, [id]: false }));
    }
  }

  async function handleMarkAll() {
    if (!me?.userId) return;
    setLoading(true);
    try {
      await markAllNotificationsRead(me.userId);
      // jednostavno isprazni listu ili refetch ako hoćeš
      const list = await getNotifications({ userId: me.userId, unread: false, limit: 200 });
      setRows(list || []);
    } catch (e) {
      setError(e?.response?.data?.message || e.message);
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="page">
      <div className="container">
        <header className="page-head">
          <div>
            <h1>Notifikacije</h1>
            <p className="muted">
              {me ? `${me.firstName} ${me.lastName} • ${me.userEmail}` : "Učitavanje korisnika..."}
            </p>
          </div>
          <div className="head-actions">
            <a href="/" className="btn ghost">Nazad na početnu</a>
            <button className="btn primary" onClick={handleMarkAll} disabled={!rows.length || loading}>
              Označi sve kao pročitano
            </button>
          </div>
        </header>

        <div className="card">
          {loading ? (
            <div className="skeleton-list">
              <div className="skeleton" />
              <div className="skeleton" />
              <div className="skeleton" />
            </div>
          ) : error ? (
            <div className="error">{error}</div>
          ) : rows.length === 0 ? (
            <p className="muted empty">Nema notifikacija.</p>
          ) : (
            <div className="table-wrap">
              <table className="table">
                <thead>
                  <tr>
                    <th style={{ width: 64 }}>#</th>
                    <th>Naslov</th>
                    <th>Poruka</th>
                    <th style={{ width: 180 }}>Poslato</th>
                    <th style={{ width: 150 }}></th>
                  </tr>
                </thead>
                <tbody>
                  {rows.map((n, idx) => {
                    const id = n.notificationId ?? n.id;
                    return (
                      <tr key={id}>
                        <td>{idx + 1}</td>
                        <td className="title-cell">{n.notificationTitle || n.title}</td>
                        <td className="message-cell">{n.message || n.notificationMessage || n.body}</td>
                        <td className="date-cell">
                          {n.sentAt ? new Date(n.sentAt).toLocaleString() : "—"}
                        </td>
                        <td className="action-cell">
                          <button
                            className="btn sm"
                            disabled={!!marking[id]}
                            onClick={() => handleMarkRead(id)}
                          >
                            {marking[id] ? "..." : "Označi pročitano"}
                          </button>
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
