import React, { useEffect, useState } from "react";
import { Link, NavLink, useNavigate } from "react-router-dom";
import { getMe, getUnreadCount } from "../api/api";
import { onUnreadChanged } from "../api/notificationsBus";

function getStoredUser() {
  try { return JSON.parse(localStorage.getItem("user") || "null"); } catch { return null; }
}

export default function Navbar() {
  const [me, setMe] = useState(() => getStoredUser());
  const [badge, setBadge] = useState(0);
  const navigate = useNavigate();

  const token = typeof window !== "undefined" ? localStorage.getItem("token") : null;
  const isLoggedIn = !!token;

  useEffect(() => {
    if (!isLoggedIn) return;

    let alive = true;

    // 1) Tiho osveÅ¾i /me (bez logout-a ako pukne)
    (async () => {
      try {
        const fresh = await getMe();
        if (!alive) return;
        setMe(fresh);
        try { localStorage.setItem("user", JSON.stringify(fresh)); } catch { }
      } catch {
        // ignoriÅ¡i â€“ ne odjavljuj ovde
      }
    })();

    // 2) Badge (ako imamo bar neki user iz LS)
    (async () => {
      try {
        const u = getStoredUser() || me;
        const id = u?.id || u?.userId;
        if (!id) return;
        const c = await getUnreadCount(id);
        if (alive) setBadge(c);
      } catch { }
    })();

    const off = onUnreadChanged(async () => {
      try {
        const u = getStoredUser() || me;
        const id = u?.id || u?.userId;
        if (!id) return;
        const c = await getUnreadCount(id);
        if (alive) setBadge(c);
      } catch { }
    });

    return () => { alive = false; off?.(); };
  }, [isLoggedIn]);

  function handleLogout() {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/login");
  }

  const name =
    [me?.firstName, me?.lastName].filter(Boolean).join(" ") ||
    me?.username ||
    "User";

  const active = ({ isActive }) => ({
    textDecoration: "none",
    opacity: isActive ? 1 : 0.85,
  });

  // ------------------ RENDER ------------------
  return (
    <nav className="nav">
      <div className="container nav-inner">
        {/* BRAND */}
        <Link to="/" className="brand">
          E-Learning
        </Link>

        {/* IF NOT LOGGED IN */}
        {!isLoggedIn && (
          <div className="nav-right">
            <Link className="btn ghost sm" to="/login">
              Sign in
            </Link>
            <Link className="btn primary sm" to="/register">
              Register
            </Link>
          </div>
        )}

        {/* IF LOGGED IN */}
        {isLoggedIn && (
          <>
            <div className="nav-links">
              <NavLink to="/" style={active}>
                Home
              </NavLink>
              <NavLink to="/notifications" style={active}>
                Notifications
              </NavLink>
            </div>

            <div className="nav-right">
              {/* Bell */}
              <Link to="/notifications" className="bell" title="Notifications">
                ðŸ””
                {badge > 0 && <span className="badge-dot">{badge}</span>}
              </Link>

              {/* Username chip */}
              <span className="user-chip" title={userLabel}>
                {userLabel}
              </span>

              {/* Logout */}
              <button className="btn ghost sm" onClick={handleLogout}>
                Logout
              </button>
            </div>
          </>
        )}
      </div>
    </nav>
  );
}
