import React, { useEffect, useState } from "react";
import { getMe, getUnreadCount } from "../api/api";

export default function Navbar() {
    const [count, setCount] = useState(0);

    useEffect(() => {
        let active = true;

        async function load() {
            try {
                const me = await getMe({ userId: 1 }); // dok nema auth-a
                const n = await getUnreadCount(me.userId);
                if (active) setCount(Number(n || 0));
            } catch {
                // ignore
            }
        }

        load();

        // osluÅ¡kuj refresh iz Notifications stranice
        const onUpdated = (e) => setCount(Number(e.detail || 0));
        window.addEventListener("unread-updated", onUpdated);

        return () => {
            active = false;
            window.removeEventListener("unread-updated", onUpdated);
        };
    }, []);

    return (
        <nav className="nav">
            <div className="container nav-inner">
                <a className="brand" href="/">E-Learning</a>

                <div className="nav-links">
                    <a href="/catalog">Katalog</a>
                    <a href="/my-courses">Moji kursevi</a>
                </div>

                <div className="nav-right">
                    <a className="bell" href="/notifications" aria-label="Notifikacije">
                        ðŸ””
                        {count > 0 && <span className="badge-dot">{count}</span>}
                    </a>

                    <div className="avatar">U</div>
                </div>
            </div>
        </nav>
    );
}
