import React, { useEffect, useState } from "react";
import { Link, NavLink } from "react-router-dom";
import { getMe, getUnreadCount } from "../api/api";
import { onUnreadChanged } from "../api/notificationsBus";
import "../App.css";

export default function Navbar() {
  const [me, setMe] = useState(null);
  const [badge, setBadge] = useState(0);

  useEffect(() => {
    let alive = true;
    (async () => {
      try {
        const u = await getMe();
        if (!alive) return;
        setMe(u);
      } catch { }
      try {
        const c = await getUnreadCount();
        if (!alive) return;
        setBadge(c);
      } catch { }
    })();

    const off = onUnreadChanged(async () => {
      try {
        const c = await getUnreadCount();
        if (alive) setBadge(c);
      } catch { }
    });

    return () => {
      alive = false;
      off?.();
    };
  }, []);

  const username =
    me?.username || [me?.firstName, me?.lastName].filter(Boolean).join(" ") || "User";

  const active = ({ isActive }) => ({
    textDecoration: "none",
    opacity: isActive ? 1 : 0.85,
  });

  return (
    <nav className="nav">
      <div className="container nav-inner">
        <Link to="/" className="brand">E-Learning</Link>

        <div className="nav-links">
          <NavLink to="/" style={active}>Home</NavLink>
          <NavLink to="/notifications" style={active}>Notifications</NavLink>
        </div>

        <div className="nav-right">
          <Link to="/notifications" className="bell" title="Notifications">
            ðŸ”” {badge > 0 && <span className="badge-dot">{badge}</span>}
          </Link>
          <span className="user-chip" title={username}>{username}</span>
        </div>
      </div>
    </nav>
  );
}
