import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { getMe, getUnreadCount } from "../api/api";
import { onUnreadChanged } from "../api/notificationsBus";

// usaglasi sa Home / Notifications – dok nema auth
const FAKE_USER_ID = 2;

export default function Navbar() {
    const [me, setMe] = useState(null);
    const [unread, setUnread] = useState(0);

    async function refreshBadge() {
        try {
            const count = await getUnreadCount(FAKE_USER_ID);
            setUnread(count);
        } catch {
            // bez drame
        }
    }

    useEffect(() => {
        // učitaj korisnika (opciono, zbog inicijala u avataru)
        getMe({ userId: FAKE_USER_ID }).then(setMe).catch(() => { });
        // prvi load bedža
        refreshBadge();

        // sluša promene sa drugih ekrana
        const off = onUnreadChanged(() => {
            refreshBadge();
        });

        return off;
    }, []);

    return (
        <nav className="nav">
            <div className="container nav-inner">
                <Link className="brand" to="/">E-Learning</Link>

                <div className="nav-links">
                    <Link to="/catalog">Katalog</Link>
                    <Link to="/my-courses">Moji kursevi</Link>
                </div>

                <div className="nav-right">
                    <Link className="bell" to="/notifications" title="Notifikacije">🔔
                        {unread > 0 && <span className="badge-dot">{unread}</span>}
                    </Link>

                    <div className="avatar">{/* inicijali */}
                        {(me?.firstName?.[0] || "U").toUpperCase()}
                        {(me?.lastName?.[0] || "").toUpperCase()}
                    </div>
                </div>
            </div>
        </nav>
    );
}
