// src/api/http.js
import axios from "axios";

const http = axios.create({
    baseURL: "http://localhost:8080/api",
    headers: { "Content-Type": "application/json" },
});

http.interceptors.request.use((config) => {
    const token = typeof window !== "undefined" ? localStorage.getItem("token") : null;
    if (token) {
        config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
});

http.interceptors.response.use(
    (res) => res,
    (error) => {
        const status = error?.response?.status;
        const url = (error?.config?.url || "").toLowerCase();

        const isAuthLogin = url.includes("/auth/login");
        const isAuthRegister = url.includes("/auth/register");
        const isAuthMe = url.includes("/auth/me");

        // Ako token istekne na drugim API-jima -> odjavi i pošalji na /login.
        // Ali NEMOJ to raditi kad puca baš /auth/me (to nam je "best effort").
        if ((status === 401 || status === 403) && !isAuthLogin && !isAuthRegister) {
            if (!isAuthMe) {
                try {
                    localStorage.removeItem("token");
                    localStorage.removeItem("user");
                } catch { }
                if (typeof window !== "undefined" && window.location.pathname !== "/login") {
                    window.location.href = "/login";
                }
            }
        }

        return Promise.reject(error);
    }
);

export default http;
