import React, { useEffect, useMemo, useState } from "react";
import { Link } from "react-router-dom";
import {
  getMe,
  getNotifications,         // čita SVE notifikacije korisnika
  markNotificationRead,      // označava jednu
  markAllNotificationsRead,  // označava sve
} from "../api/api";
import "../css/Notifications.css";

function getTypeName(n) {
  // fleksibilno čitanje tipa (zavisno kako DTO izgleda)
  return (
    n?.type?.name ||
    n?.notificationType?.name ||
    n?.typeName ||
    n?.type ||
    "—"
  );
}
function getTitle(n) {
  return n.notificationTitle || n.title || "";
}
function getBody(n) {
  return n.message || n.notificationMessage || n.body || "";
}

export default function Notifications() {
  const [me, setMe] = useState(null);
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState("");
  const [busy, setBusy] = useState(false);

  // UI state
  const [showUnreadOnly, setShowUnreadOnly] = useState(false);
  const [q, setQ] = useState("");
  const [sortBy, setSortBy] = useState("date_desc"); // date_desc | date_asc | title_asc | title_desc | type_asc | type_desc

  useEffect(() => {
    let uid;
    setLoading(true);
    setErr("");
    getMe({ userId: 2 })
      .then((u) => {
        setMe(u);
        uid = u.userId;
        return getNotifications(uid); // sve
      })
      .then((list) => setItems(Array.isArray(list) ? list : []))
      .catch((e) => setErr(e?.message || "Greška pri učitavanju notifikacija."))
      .finally(() => setLoading(false));
  }, []);

  // filtrirano + sortirano
  const visible = useMemo(() => {
    let arr = items;

    // 1) samo nepročitane?
    if (showUnreadOnly) arr = arr.filter((n) => !n.read);

    // 2) pretraga po naslovu / poruci / tipu
    const needle = q.trim().toLowerCase();
    if (needle) {
      arr = arr.filter((n) => {
        const title = getTitle(n).toLowerCase();
        const body = getBody(n).toLowerCase();
        const tname = getTypeName(n).toLowerCase();
        return (
          title.includes(needle) || body.includes(needle) || tname.includes(needle)
        );
      });
    }

    // 3) sortiranje
    const cmp = new Intl.Collator("sr-RS", { sensitivity: "base" }).compare;
    arr = [...arr].sort((a, b) => {
      const aDate = a.sentAt ? new Date(a.sentAt).getTime() : 0;
      const bDate = b.sentAt ? new Date(b.sentAt).getTime() : 0;
      const aTitle = getTitle(a);
      const bTitle = getTitle(b);
      const aType = getTypeName(a);
      const bType = getTypeName(b);

      switch (sortBy) {
        case "date_asc":
          return aDate - bDate;
        case "title_asc":
          return cmp(aTitle, bTitle);
        case "title_desc":
          return cmp(bTitle, aTitle);
        case "type_asc":
          return cmp(aType, bType);
        case "type_desc":
          return cmp(bType, aType);
        case "date_desc":
        default:
          return bDate - aDate;
      }
    });

    return arr;
  }, [items, showUnreadOnly, q, sortBy]);

  // označi jednu
  async function handleMarkOne(id) {
    setItems((prev) =>
      prev.map((n) =>
        (n.notificationId ?? n.id) === id ? { ...n, read: true } : n
      )
    );
    try {
      await markNotificationRead(id);
    } catch (e) {
      // rollback
      setItems((prev) =>
        prev.map((n) =>
          (n.notificationId ?? n.id) === id ? { ...n, read: false } : n
        )
      );
    }
  }

  // označi sve
  async function handleMarkAll() {
    if (!me?.userId) return;
    setBusy(true);
    const snapshot = items;
    setItems((prev) => prev.map((n) => ({ ...n, read: true })));
    try {
      await markAllNotificationsRead(me.userId);
    } catch {
      setItems(snapshot);
    } finally {
      setBusy(false);
    }
  }

  return (
    <div className="container section">
      <div className="notif-head">
        <h1>Notifikacije</h1>
        <div className="notif-actions">
          <Link className="btn ghost" to="/">
            Nazad na početnu
          </Link>
          <button className="btn primary" onClick={handleMarkAll} disabled={busy}>
            Označi sve kao pročitano
          </button>
        </div>
      </div>

      <div className="muted" style={{ marginBottom: 16 }}>
        {me ? (
          <>
            {me.firstName} {me.lastName} • {me.userEmail}
          </>
        ) : (
          <>Učitavanje korisnika…</>
        )}
      </div>

      {/* Toolbar: pretraga, filter i sort */}
      <div className="toolbar-row toolbar-grid">
        <div className="search-wrap">
          <input
            className="input"
            type="text"
            placeholder="Pretraga po naslovu, poruci ili tipu…"
            value={q}
            onChange={(e) => setQ(e.target.value)}
          />
          {q && (
            <button className="clear" onClick={() => setQ("")} aria-label="Obriši pretragu">
              ×
            </button>
          )}
        </div>

        <label className="toggle">
          <input
            type="checkbox"
            checked={showUnreadOnly}
            onChange={(e) => setShowUnreadOnly(e.target.checked)}
          />
          <span>Samo nepročitane</span>
        </label>

        <div className="sort-wrap">
          <label className="sort-label">Sort:</label>
          <select
            className="select"
            value={sortBy}
            onChange={(e) => setSortBy(e.target.value)}
          >
            <option value="date_desc">Datum ↓ (najnovije)</option>
            <option value="date_asc">Datum ↑ (najstarije)</option>
            <option value="title_asc">Naslov A–Z</option>
            <option value="title_desc">Naslov Z–A</option>
            <option value="type_asc">Tip A–Z</option>
            <option value="type_desc">Tip Z–A</option>
          </select>
        </div>
      </div>

      {err && <div className="alert error">{err}</div>}
      {loading ? (
        <div className="skeleton large" />
      ) : visible.length === 0 ? (
        <p className="muted">Nema notifikacija za prikaz.</p>
      ) : (
        <table className="notif-table card">
          <thead>
            <tr>
              <th>#</th>
              <th>Naslov</th>
              <th>Tip</th>
              <th>Poruka</th>
              <th>Poslato</th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {visible.map((n, idx) => {
              const id = n.notificationId ?? n.id;
              const sent = n.sentAt ? new Date(n.sentAt).toLocaleString() : "";
              const tname = getTypeName(n);

              return (
                <tr
                  key={id}
                  className={n.read ? "read" : "unread"}
                  title={n.read ? "Pročitano" : "Nepročitano"}
                >
                  <td>{idx + 1}</td>
                  <td className="title">{getTitle(n)}</td>
                  <td className="type">
                    <span className="pill">{tname}</span>
                  </td>
                  <td className="body">{getBody(n)}</td>
                  <td className="when">{sent}</td>
                  <td>
                    {!n.read ? (
                      <button
                        className="btn ghost sm"
                        onClick={() => handleMarkOne(id)}
                      >
                        Označi pročitano
                      </button>
                    ) : (
                      <span className="badge">Pročitano</span>
                    )}
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      )}
    </div>
  );
}
