// src/App.js
import React, { useEffect, useState } from "react";
import { BrowserRouter, Routes, Route, Navigate, Link } from "react-router-dom";
import { setUserIdHeader } from "./api/api";


setUserIdHeader(1); // dok nema auth-a


import Navbar from "./components/Navbar";
import Footer from "./components/Footer";

import Home from "./pages/Home";
import Notifications from "./pages/Notifications";

import "./App.css";

// Jednostavni placeholder-i da linkovi ne pucaju dok ne napravimo strane
const Catalog = () => (
  <div className="container section">
    <h2>Katalog</h2>
    <p className="muted">Ovde će ići lista kurseva.</p>
  </div>
);

const MyCourses = () => (
  <div className="container section">
    <h2>Moji kursevi</h2>
    <p className="muted">Ovde će ići tvoji kupljeni/prijavljeni kursevi.</p>
    <Link className="btn ghost" to="/catalog">Idi na katalog</Link>
  </div>
);

// Mali switch za temu (zelena ⇄ crvena)
function ThemeSwitch({ theme, setTheme }) {
  const isRed = theme === "red";
  return (
    <button
      className="btn ghost sm"
      onClick={() => setTheme(isRed ? "default" : "red")}
      style={{
        position: "fixed",
        right: "16px",
        bottom: "16px",
        zIndex: 1000,
      }}
      title="Promeni temu"
    >
      🎨 {isRed ? "Zelena tema" : "Crvena tema"}
    </button>
  );
}

export default function App() {
  // Tema: čita iz localStorage, upisuje pri promeni
  const [theme, setTheme] = useState(() => {
    return localStorage.getItem("theme") || "default";
  });

  useEffect(() => {
    document.body.classList.toggle("theme-red", theme === "red");
    localStorage.setItem("theme", theme);
  }, [theme]);

  return (
    <BrowserRouter>
      <Navbar />

      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/notifications" element={<Notifications />} />

        {/* Placeholder rute, slobodno kasnije zameni stvarnim stranicama */}
        <Route path="/catalog" element={<Catalog />} />
        <Route path="/my-courses" element={<MyCourses />} />

        {/* fallback */}
        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>

      <Footer />
      <ThemeSwitch theme={theme} setTheme={setTheme} />
    </BrowserRouter>
  );
}
