import React, { useEffect, useMemo, useState } from "react";
import { useNavigate } from "react-router-dom";
import {
    getMe,
    getUsers,
    getNotificationTypes,
} from "../api/api";
import "../css/AddNotification.css";

// Dok nema auth-a:
const FAKE_USER_ID = 2;

export default function NewNotificationForm() {
    const nav = useNavigate();

    const [me, setMe] = useState(null);

    // forma
    const [title, setTitle] = useState("");
    const [message, setMessage] = useState("");
    const [userId, setUserId] = useState("");
    const [typeId, setTypeId] = useState("");

    // podaci za dropdown-e
    const [allUsers, setAllUsers] = useState([]);
    const [types, setTypes] = useState([]);

    const [sending, setSending] = useState(false);
    const [errors, setErrors] = useState({});

    // ------------------------------------------------------------------ load
    useEffect(() => {
        (async () => {
            try {
                const u = await getMe({ userId: FAKE_USER_ID });
                setMe(u);

                const [users, tps] = await Promise.all([
                    getUsers(),
                    getNotificationTypes(),
                ]);

                setAllUsers(Array.isArray(users) ? users : []);
                setTypes(Array.isArray(tps) ? tps : []);
            } catch (e) {
                console.error(e);
            }
        })();
    }, []);

    // Filtriraj: â€žsvi osim meneâ€œ
    const selectableUsers = useMemo(() => {
        if (!me) return allUsers;
        return (allUsers || []).filter(u => Number(u.userId) !== Number(me.userId));
    }, [allUsers, me]);

    // labela u combobox-u
    function userLabel(u) {
        const name = [u.firstName, u.lastName].filter(Boolean).join(" ");
        return `${name || "Korisnik"} â€¢ ${u.userEmail || ""}`.trim();
    }

    // validacija
    function validate() {
        const e = {};
        if (!title.trim()) e.title = "Naslov je obavezan.";
        if (!message.trim()) e.message = "Poruka je obavezna.";
        if (!userId) e.userId = "Izaberi korisnika.";
        if (!typeId) e.typeId = "Izaberi tip notifikacije.";
        setErrors(e);
        return Object.keys(e).length === 0;
    }

    // submit
    async function handleSubmit(e) {
        e.preventDefault();
        if (!validate()) return;

        setSending(true);
        try {
            const payload = {
                title,
                message,
                userId: Number(userId),
                notificationTypeId: Number(typeId),
            };

            // pozovi backend kreiranje
            const res = await fetch(`/api/notifications`, {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(payload),
            });
            if (!res.ok) {
                const t = await res.text().catch(() => "");
                throw new Error(t || "GreÅ¡ka pri slanju notifikacije.");
            }

            // nazad na listu
            nav("/notifications");
        } catch (err) {
            console.error(err);
            alert(err.message || "GreÅ¡ka pri slanju notifikacije.");
        } finally {
            setSending(false);
        }
    }

    return (
        <div className="nf-wrap">
            <form className="nf-card" onSubmit={handleSubmit}>
                <div className="nf-title">
                    <span className="nf-emoji">ðŸ“£</span> PoÅ¡alji novu notifikaciju
                </div>

                <label className="nf-label">Naslov notifikacije</label>
                <input
                    className={`nf-input ${errors.title ? "invalid" : ""}`}
                    placeholder="Naslov notifikacije"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                />
                {errors.title && <div className="nf-err">{errors.title}</div>}

                <label className="nf-label">Poruka</label>
                <textarea
                    className={`nf-input nf-textarea ${errors.message ? "invalid" : ""}`}
                    placeholder="Poruka"
                    rows={6}
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                />
                {errors.message && <div className="nf-err">{errors.message}</div>}

                <div className="nf-row">
                    <div className="nf-col">
                        <label className="nf-label">Korisnik</label>
                        <select
                            className={`nf-input ${errors.userId ? "invalid" : ""}`}
                            value={userId}
                            onChange={(e) => setUserId(e.target.value)}
                        >
                            <option value="">â€” Izaberi korisnika â€”</option>
                            {selectableUsers.map((u) => (
                                <option key={u.userId} value={u.userId}>
                                    {userLabel(u)}
                                </option>
                            ))}
                        </select>
                        {errors.userId && <div className="nf-err">{errors.userId}</div>}
                    </div>

                    <div className="nf-col">
                        <label className="nf-label">Tip</label>
                        <select
                            className={`nf-input ${errors.typeId ? "invalid" : ""}`}
                            value={typeId}
                            onChange={(e) => setTypeId(e.target.value)}
                        >
                            <option value="">â€” Izaberi tip â€”</option>
                            {(types || []).map((t) => (
                                <option key={t.id} value={t.id}>
                                    {t.notificationTypeName || t.name}
                                </option>
                            ))}
                        </select>
                        {errors.typeId && <div className="nf-err">{errors.typeId}</div>}
                    </div>
                </div>

                <div className="nf-actions">
                    <button
                        type="button"
                        className="btn ghost"
                        onClick={() => nav("/notifications")}
                    >
                        Odustani
                    </button>

                    <button className="btn primary" disabled={sending}>
                        {sending ? "Slanjeâ€¦" : "PoÅ¡alji"}
                    </button>
                </div>
            </form>
        </div>
    );
}
