import axios from "axios";

// Kreira se "instance" axios klijenta sa podrazumevanim URL-om i headerima
const http = axios.create({
    baseURL: "/api", // sve što pozoveš ide na tvoj backend /api/...
    headers: { "Content-Type": "application/json" },
});

export default http;
