// src/api/http.js
import axios from "axios";

/**
 * Ako koristiš Vite proxy (preporuka), u vite.config.js dodaj:
 *   server: { proxy: { '/api': { target: 'http://localhost:8080', changeOrigin: true } } }
 * Tada baseURL ostaje '/api'.
 * Ako NE koristiš proxy, zameni baseURL u 'http://localhost:8080/api'.
 */
const http = axios.create({
    baseURL: "/api",
    headers: { "Content-Type": "application/json" },
});

// (opciono) interceptor da uvek vrati .data i smanji try/catch šum
http.interceptors.response.use(
    (res) => res,
    (err) => Promise.reject(err)
);

export default http;
