// src/pages/Notifications.jsx
import React, { useEffect, useMemo, useState } from "react";
import {
  getMe,
  getNotifications,
  markNotificationRead,
  markAllNotificationsRead,
  getNotificationTypes,
  deleteNotification,
} from "../api/api";
import { emitUnreadChanged } from "../api/notificationsBus";
import "../css/Notifications.css";
import { useNavigate } from "react-router-dom";


const FAKE_USER_ID = 2; // dok nema auth

export default function Notifications() {
  const navigate = useNavigate();

  const [me, setMe] = useState(null);
  const [items, setItems] = useState([]);
  const [types, setTypes] = useState([]);
  const [loading, setLoading] = useState(true);

  const [onlyUnread, setOnlyUnread] = useState(false);
  const [query, setQuery] = useState("");
  const [sortKey, setSortKey] = useState("date_desc");
  const [typeFilter, setTypeFilter] = useState("all");

  // pagination
  const [page, setPage] = useState(0); // 0-indexed
  const [rowsPerPage, setRowsPerPage] = useState(100); // default as on screenshot
  const rowsOptions = [10, 25, 50, 100];

  // load
  useEffect(() => {
    let uid;
    async function load() {
      try {
        setLoading(true);
        const u = await getMe({ userId: FAKE_USER_ID });
        setMe(u);
        uid = u?.userId ?? FAKE_USER_ID;

        const [tps, list] = await Promise.all([
          getNotificationTypes(),
          getNotifications({ userId: uid, unread: false, limit: 1000 }),
        ]);

        setTypes(Array.isArray(tps) ? tps : []);
        setItems(Array.isArray(list) ? list : []);
        setPage(0);
      } catch (e) {
        console.error("Error loading notifications:", e);
        setItems([]);
      } finally {
        setLoading(false);
      }
    }

    load();
  }, []);

  // types map
  const typesMap = useMemo(() => {
    const m = new Map();
    for (const t of types || []) {
      if (!t) continue;
      const id = Number(t.id);
      if (Number.isFinite(id)) {
        m.set(id, t.notificationTypeName || t.name || "");
      }
    }
    return m;
  }, [types]);

  const getTypeId = (n) =>
    n?.notificationTypeId ??
    n?.typeId ??
    n?.type?.id ??
    n?.notificationType?.id ??
    null;

  // visible after filters + sort
  const visible = useMemo(() => {
    let arr = Array.isArray(items) ? [...items] : [];

    if (onlyUnread) arr = arr.filter((n) => !n.read);

    if (typeFilter !== "all") {
      const idNum = Number(typeFilter);
      arr = arr.filter((n) => Number(getTypeId(n)) === idNum);
    }

    if (query.trim()) {
      const q = query.trim().toLowerCase();
      arr = arr.filter((n) => {
        const title = (n.notificationTitle || n.title || "").toLowerCase();
        const msg = (n.message || n.notificationMessage || n.body || "").toLowerCase();
        const tName = (typesMap.get(Number(getTypeId(n))) || "").toLowerCase();
        return title.includes(q) || msg.includes(q) || tName.includes(q);
      });
    }

    arr.sort((a, b) => {
      switch (sortKey) {
        case "title_asc":
        case "title_desc": {
          const A = (a.notificationTitle || a.title || "").toLowerCase();
          const B = (b.notificationTitle || b.title || "").toLowerCase();
          const cmp = A.localeCompare(B);
          return sortKey === "title_asc" ? cmp : -cmp;
        }
        case "type_asc":
        case "type_desc": {
          const A = (typesMap.get(Number(getTypeId(a))) || "").toLowerCase();
          const B = (typesMap.get(Number(getTypeId(b))) || "").toLowerCase();
          const cmp = A.localeCompare(B);
          return sortKey === "type_asc" ? cmp : -cmp;
        }
        case "date_asc":
        case "date_desc":
        default: {
          const A = new Date(a.sentAt || a.createdAt || a.date || 0).getTime();
          const B = new Date(b.sentAt || b.createdAt || b.date || 0).getTime();
          return sortKey === "date_asc" ? A - B : B - A;
        }
      }
    });

    return arr;
  }, [items, onlyUnread, query, sortKey, typeFilter, typesMap]);

  // reset page when filters change or rowsPerPage change to keep valid start
  useEffect(() => {
    setPage(0);
  }, [onlyUnread, query, sortKey, typeFilter, rowsPerPage, items]);

  // pagination slice
  const total = visible.length;
  const start = Math.min(page * rowsPerPage, total);
  const end = Math.min(start + rowsPerPage, total);
  const pageItems = visible.slice(start, end);

  // actions
  async function handleMarkRead(id) {
    const prev = items;
    setItems((list) =>
      list.map((n) => (n.notificationId === id || n.id === id ? { ...n, read: true } : n))
    );

    try {
      await markNotificationRead(id);
      emitUnreadChanged();
    } catch {
      setItems(prev);
    }
  }

  async function handleDelete(id) {
    const prev = items;
    setItems((list) => list.filter((n) => n.notificationId !== id && n.id !== id));

    try {
      await deleteNotification(id);
      emitUnreadChanged();
    } catch {
      alert("Greška pri brisanju notifikacije.");
      setItems(prev);
    }
  }

  async function handleMarkAll() {
    if (!me?.userId) return;
    const prev = items;
    setItems((list) => list.map((n) => ({ ...n, read: true })));

    try {
      await markAllNotificationsRead(me.userId);
      emitUnreadChanged();
    } catch {
      setItems(prev);
    }
  }

  // pagination nav handlers
  function goPrev() {
    setPage((p) => Math.max(0, p - 1));
  }
  function goNext() {
    const maxPage = Math.max(0, Math.ceil(total / rowsPerPage) - 1);
    setPage((p) => Math.min(maxPage, p + 1));
  }
  function handleRowsChange(e) {
    setRowsPerPage(Number(e.target.value));
    setPage(0);
  }

  return (
    <div className="page-wrap">
      <header className="n-head">
        <div className="n-left">
          <div className="who">
            {me?.firstName} {me?.lastName} • <span className="muted">{me?.userEmail}</span>
          </div>

          <div className="n-filters">
            <input
              className="n-search"
              placeholder="Pretraga po naslovu, poruci ili tipu…"
              value={query}
              onChange={(e) => setQuery(e.target.value)}
            />

            <label className="n-check">
              <input
                type="checkbox"
                checked={onlyUnread}
                onChange={(e) => setOnlyUnread(e.target.checked)}
              />
              <span>Samo nepročitane</span>
            </label>

            <select
              className="n-select"
              value={typeFilter}
              onChange={(e) => setTypeFilter(e.target.value)}
            >
              <option value="all">Svi tipovi</option>
              {(types || []).map((t) => (
                <option key={t.id} value={t.id}>
                  {t.notificationTypeName || t.name}
                </option>
              ))}
            </select>

            <select
              className="n-select"
              value={sortKey}
              onChange={(e) => setSortKey(e.target.value)}
            >
              <option value="date_desc">Datum (novije prvo)</option>
              <option value="date_asc">Datum (starije prvo)</option>
              <option value="title_asc">Naslov A–Z</option>
              <option value="title_desc">Naslov Z–A</option>
              <option value="type_asc">Tip A–Z</option>
              <option value="type_desc">Tip Z–A</option>
            </select>
          </div>
        </div>

        <div className="n-right">
          <button className="btn primary" onClick={handleMarkAll}>
            Označi sve kao pročitano
          </button>
        </div>
      </header>

      <div className="card n-card">
        <div className="n-table head">
          <div>#</div>
          <div>Naslov</div>
          <div>Tip</div>
          <div>Poruka</div>
          <div>Poslato</div>
          <div>Akcije</div>
        </div>

        {loading ? (
          <div className="n-skeleton">Učitavanje…</div>
        ) : (
          pageItems.map((n, idx) => {
            const id = n.notificationId ?? n.id;
            const title = n.notificationTitle || n.title || "—";
            const msg = n.message || n.notificationMessage || n.body || "—";
            const sent = n.sentAt || n.createdAt || n.date;
            const tId = Number(getTypeId(n));
            const tName = typesMap.get(tId) || "—";

            return (
              <div key={id} className={`n-table row ${n.read ? "is-read" : ""}`}>
                <div>{start + idx + 1}</div>
                <div className="n-title">{title}</div>
                <div>
                  <span className="type-pill">{tName}</span>
                </div>
                <div className="n-msg">{msg}</div>
                <div className="n-date">{sent ? new Date(sent).toLocaleString() : "—"}</div>
                <div className="n-actions">
                  {n.read ? (
                    <span className="badge read">Pročitano</span>
                  ) : (
                    <button className="btn ghost sm" onClick={() => handleMarkRead(id)}>
                      Označi pročitano
                    </button>
                  )}
                  <button
                    className="btn ghost sm danger"
                    onClick={() => {
                      if (window.confirm("Da li stvarno želiš da obrišeš notifikaciju?")) {
                        handleDelete(id);
                      }
                    }}
                    style={{ marginLeft: 8 }}
                  >
                    Obriši
                  </button>
                </div>
              </div>
            );
          })
        )}

        {!loading && pageItems.length === 0 && (
          <div className="n-empty muted">Nema rezultata za dati filter/pretragu.</div>
        )}

        {/* Pagination footer */}
        <div className="n-pagination">
          <div className="n-pagination-left">
            <label>
              Rows per page:&nbsp;
              <select value={rowsPerPage} onChange={handleRowsChange}>
                {rowsOptions.map((r) => (
                  <option key={r} value={r}>
                    {r}
                  </option>
                ))}
              </select>
            </label>
          </div>

          <div className="n-pagination-center">
            {total === 0 ? (
              <span>0 of 0</span>
            ) : (
              <span>
                {total === 0 ? 0 : start + 1}–{end} of {total}
              </span>
            )}
          </div>

          <div className="n-pagination-right">
            <button className="btn ghost sm" onClick={goPrev} disabled={page <= 0}>
              ◀
            </button>
            <button
              className="btn ghost sm"
              onClick={goNext}
              disabled={end >= total || total === 0}
              style={{ marginLeft: 6 }}
            >
              ▶
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
