import React, { useEffect, useMemo, useState } from "react";
import { Link } from "react-router-dom";
import {
  getMe,
  getNotifications,         // <— univerzalni fetch
  markNotificationRead,      // <— označi jednu
  markAllNotificationsRead,  // <— označi sve
} from "../api/api";
import "../css/Notifications.css";

export default function Notifications() {
  const [me, setMe] = useState(null);
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState("");
  const [busy, setBusy] = useState(false);
  const [showUnreadOnly, setShowUnreadOnly] = useState(false); // <— prekidač

  // učitavanje korisnika + svih notifikacija (bez unread filtera!)
  useEffect(() => {
    let uid;
    setLoading(true);
    setErr("");
    getMe({ userId: 1 })
      .then((u) => {
        setMe(u);
        uid = u.userId;
        return getNotifications(uid); // <— BITNO: bez { unread: true }
      })
      .then((list) => setItems(Array.isArray(list) ? list : []))
      .catch((e) => setErr(e?.message || "Greška pri učitavanju."))
      .finally(() => setLoading(false));
  }, []);

  // filtrirani prikaz (UI)
  const visible = useMemo(() => {
    return showUnreadOnly ? items.filter((n) => !n.read) : items;
  }, [items, showUnreadOnly]);

  async function handleMarkOne(id) {
    // optimistički: ažuriraj read u state-u, ne uklanjaj stavku
    setItems((prev) =>
      prev.map((n) =>
        (n.notificationId ?? n.id) === id ? { ...n, read: true } : n
      )
    );
    try {
      await markNotificationRead(id);
    } catch (e) {
      // rollback ako backend padne
      setItems((prev) =>
        prev.map((n) =>
          (n.notificationId ?? n.id) === id ? { ...n, read: false } : n
        )
      );
    }
  }

  async function handleMarkAll() {
    if (!me?.userId) return;
    setBusy(true);
    // optimistički: sve na read=true
    const snapshot = items;
    setItems((prev) => prev.map((n) => ({ ...n, read: true })));
    try {
      await markAllNotificationsRead(me.userId);
    } catch {
      // rollback
      setItems(snapshot);
    } finally {
      setBusy(false);
    }
  }

  return (
    <div className="container section">
      <div className="notif-head">
        <h1>Notifikacije</h1>
        <div className="notif-actions">
          <Link className="btn ghost" to="/">Nazad na početnu</Link>
          <button className="btn primary" onClick={handleMarkAll} disabled={busy}>
            Označi sve kao pročitano
          </button>
        </div>
      </div>

      <div className="muted" style={{ marginBottom: 16 }}>
        {me ? (
          <>
            {me.firstName} {me.lastName} • {me.userEmail}
          </>
        ) : (
          <>Učitavanje korisnika…</>
        )}
      </div>

      {/* Prekidač: samo nepročitane */}
      <div className="toolbar-row">
        <label className="toggle">
          <input
            type="checkbox"
            checked={showUnreadOnly}
            onChange={(e) => setShowUnreadOnly(e.target.checked)}
          />
          <span>Samo nepročitane</span>
        </label>
      </div>

      {err && <div className="alert error">{err}</div>}
      {loading ? (
        <div className="skeleton large" />
      ) : visible.length === 0 ? (
        <p className="muted">Nema notifikacija za prikaz.</p>
      ) : (
        <div className="table card">
          <div className="thead">
            <div>#</div>
            <div>Naslov</div>
            <div>Poruka</div>
            <div>Poslato</div>
            <div />
          </div>
          <div className="tbody">
            {visible.map((n, idx) => {
              const id = n.notificationId ?? n.id;
              const sent = n.sentAt
                ? new Date(n.sentAt).toLocaleString()
                : "";
              return (
                <div
                  key={id}
                  className={`row ${n.read ? "read" : "unread"}`}
                  title={n.read ? "Pročitano" : "Nepročitano"}
                >
                  <div>{idx + 1}</div>
                  <div className="title">{n.notificationTitle || n.title}</div>
                  <div className="body">{n.message || n.notificationMessage || n.body}</div>
                  <div className="when">{sent}</div>
                  <div className="actions">
                    {!n.read ? (
                      <button className="btn ghost sm" onClick={() => handleMarkOne(id)}>
                        Označi pročitano
                      </button>
                    ) : (
                      <span className="badge">Pročitano</span>
                    )}
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      )}
    </div>
  );
}
