// src/components/Navbar.jsx
import React, { useEffect, useState } from "react";
import { Link, NavLink, useNavigate } from "react-router-dom";
import { getMe, getUnreadCount } from "../api/api";
import { onUnreadChanged } from "../api/notificationsBus";
import "../App.css";

export default function Navbar() {
  const [me, setMe] = useState(null);
  const [badge, setBadge] = useState(0);
  const navigate = useNavigate();

  const token =
    typeof window !== "undefined" ? localStorage.getItem("token") : null;
  const isLoggedIn = !!token;

  // ------------------ LOAD USER + BADGE ------------------
  useEffect(() => {
    let mounted = true;
    if (!isLoggedIn) return;

    async function load() {
      try {
        const u = await getMe();
        if (!mounted) return;
        setMe(u);

        const count = await getUnreadCount(u.id || u.userId);
        if (!mounted) return;
        setBadge(count);
      } catch {
        // ako token više nije validan, očisti storage i pošalji na login
        localStorage.removeItem("token");
        localStorage.removeItem("user");
        navigate("/login");
      }
    }

    load();

    const off = onUnreadChanged(async () => {
      try {
        const id = me?.id || me?.userId;
        if (!id) return;
        const c = await getUnreadCount(id);
        if (mounted) setBadge(c);
      } catch { }
    });

    return () => {
      mounted = false;
      off?.();
    };
  }, [isLoggedIn, me?.id, me?.userId, navigate]);

  // ------------------ LOGOUT HANDLER ------------------
  function handleLogout() {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/login");
  }

  // ------------------ UI HELPERS ------------------
  const name =
    [me?.firstName, me?.lastName].filter(Boolean).join(" ") ||
    me?.username ||
    "";
  const userLabel = name || "User";

  const active = ({ isActive }) => ({
    textDecoration: "none",
    opacity: isActive ? 1 : 0.85,
  });

  // ------------------ RENDER ------------------
  return (
    <nav className="nav">
      <div className="container nav-inner">
        {/* BRAND */}
        <Link to="/" className="brand">
          E-Learning
        </Link>

        {/* IF NOT LOGGED IN */}
        {!isLoggedIn && (
          <div className="nav-right">
            <Link className="btn ghost sm" to="/login">
              Sign in
            </Link>
            <Link className="btn primary sm" to="/register">
              Register
            </Link>
          </div>
        )}

        {/* IF LOGGED IN */}
        {isLoggedIn && (
          <>
            <div className="nav-links">
              <NavLink to="/" style={active}>
                Home
              </NavLink>
              <NavLink to="/notifications" style={active}>
                Notifications
              </NavLink>
            </div>

            <div className="nav-right">
              {/* Bell */}
              <Link to="/notifications" className="bell" title="Notifications">
                🔔
                {badge > 0 && <span className="badge-dot">{badge}</span>}
              </Link>

              {/* Username chip */}
              <span className="user-chip" title={userLabel}>
                {userLabel}
              </span>

              {/* Logout */}
              <button className="btn ghost sm" onClick={handleLogout}>
                Logout
              </button>
            </div>
          </>
        )}
      </div>
    </nav>
  );
}
