import React, { useEffect, useState } from "react";
import {
  getMe,
  getNotifications, // ✅ koristimo getNotifications umesto getUnreadNotifications
  markNotificationRead,
  getRecommendedCourses,
} from "../api/api";


export default function Home() {
  const [me, setMe] = useState(null);
  const [notes, setNotes] = useState([]);
  const [recs, setRecs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [marking, setMarking] = useState({}); // id: boolean

  useEffect(() => {
    let uid;
    getMe({ userId: 2 })
      .then((u) => {
        setMe(u);
        uid = u.userId;
        return Promise.all([
          getNotifications({ userId: uid, unread: true, limit: 3 }), // ✅ sada koristi novu funkciju
          getRecommendedCourses(),
        ]);

      })
      .then(([n, r]) => {
        setNotes(n || []);
        setRecs(r || []);
      })
      .finally(() => setLoading(false))
      .catch(() => setLoading(false));
  }, []);

  async function handleRead(id) {
    setMarking((m) => ({ ...m, [id]: true }));
    const prev = notes;
    setNotes((arr) => arr.filter((x) => (x.notificationId || x.id) !== id));
    try {
      await markNotificationRead(id);
      try { localStorage.setItem("notifications:changed", String(Date.now())); } catch { }
    } catch (e) {
      setNotes(prev); // revert
    } finally {
      setMarking((m) => ({ ...m, [id]: false }));
    }
  }

  return (
    <>
      <section className="hero hero--compressed">
        <div className="container hero-grid">
          <div className="hero-copy">
            <h1>Ćao{me?.firstName ? `, ${me.firstName}` : ""}! 👋</h1>
            <p className="muted">Dobrodošao na svoju kontrolnu tablu.</p>

            <div className="quick-actions">
              <a className="btn primary" href="/my-courses">Idi na “Moji kursevi”</a>
              <a className="btn ghost" href="/catalog">Pogledaj katalog</a>
            </div>
          </div>

          <div className="profile-card">
            <div className="avatar xl">
              {me?.avatarUrl ? (
                <img src={me.avatarUrl} alt="Avatar" />
              ) : (
                <span>
                  {(me?.firstName?.[0] || "U").toUpperCase()}
                  {(me?.lastName?.[0] || "").toUpperCase()}
                </span>
              )}
            </div>
            <div className="profile-meta">
              <div className="profile-name">
                {me?.firstName} {me?.lastName}
              </div>
              <div className="muted">{me?.userEmail}</div>
            </div>
          </div>
        </div>
      </section>

      <section className="section">
        <div className="container grid-2">
          <div className="card">
            <div className="card-head">
              <h3>Nepročitane notifikacije</h3>
              <a className="link" href="/notifications?unread=true">Vidi sve</a>
            </div>

            {loading ? (
              <div className="skeleton-list">
                <div className="skeleton" />
                <div className="skeleton" />
                <div className="skeleton" />
              </div>
            ) : notes.length === 0 ? (
              <p className="muted">Nema novih notifikacija.</p>
            ) : (
              <ul className="note-list" role="list">
                {notes.map((n) => {
                  const id = n.notificationId ?? n.id;
                  return (
                    <li key={id} className="note-item" role="listitem">
                      <div className="note-main">
                        <div className="note-title">{n.notificationTitle || n.title}</div>
                        <div className="note-text muted">
                          {n.message || n.notificationMessage || n.body}
                        </div>
                      </div>
                      <button
                        className="btn ghost sm"
                        disabled={!!marking[id]}
                        onClick={() => handleRead(id)}
                      >
                        {marking[id] ? "..." : "Označi pročitano"}
                      </button>
                    </li>
                  );
                })}
              </ul>
            )}
          </div>

          <div className="card">
            <div className="card-head">
              <h3>Preporučeni kursevi</h3>
              <a className="link" href="/catalog">Katalog</a>
            </div>
            <div className="courses">
              {recs.map((c) => (
                <a key={c.id} className="course" href={`/course/${c.id}`}>
                  <div className="thumb" />
                  <div className="course-title">{c.title}</div>
                  <div className="muted">{c.meta}</div>
                </a>
              ))}
              {recs.length === 0 && !loading && (
                <p className="muted">Uskoro preporuke.</p>
              )}
            </div>
          </div>
        </div>
      </section>
    </>
  );
}
