import React, { useEffect, useState } from "react";
import { Link, NavLink } from "react-router-dom";
import { getMe, getUnreadCount } from "../api/api";

export default function Navbar() {
  const [me, setMe] = useState(null);
  const [unread, setUnread] = useState(0);

  async function load() {
    try {
      const u = await getMe({ userId: 2 }); // fallback dok nema auth-a
      setMe(u);
      const c = await getUnreadCount(u.userId);
      setUnread(c);
    } catch {
      /* ignore */
    }
  }

  useEffect(() => {
    load();
  }, []);

  // osveÅ¾i kad se vratiÅ¡ na tab ili posle promena (localStorage signal)
  useEffect(() => {
    const onVis = () => {
      if (document.visibilityState === "visible") load();
    };
    const onPing = () => load();

    document.addEventListener("visibilitychange", onVis);
    window.addEventListener("storage", (e) => {
      if (e.key === "notifications:changed") onPing();
    });

    return () => {
      document.removeEventListener("visibilitychange", onVis);
      window.removeEventListener("storage", onPing);
    };
  }, []);

  const initials =
    (me?.firstName?.[0] || "U").toUpperCase() +
    (me?.lastName?.[0] || "").toUpperCase();

  return (
    <nav className="nav">
      <div className="container nav-inner">
        <Link className="brand" to="/">
          ELearn
        </Link>

        <div className="nav-links">
          <NavLink to="/catalog">Katalog</NavLink>
          <NavLink to="/my-courses">Moji kursevi</NavLink>
          <NavLink to="/notifications">Notifikacije</NavLink>
        </div>

        <div className="nav-right">
          <Link className="bell" to="/notifications?unread=true" title="Notifikacije">
            ðŸ””
            {unread > 0 && <span className="badge-dot">{unread}</span>}
          </Link>
          <div className="avatar" title={me?.userEmail || ""}>
            {me?.avatarUrl ? <img src={me.avatarUrl} alt="Avatar" /> : initials}
          </div>
        </div>
      </div>
    </nav>
  );
}
